			checkErr( Ivi_InvalidateAttribute (vi, "REF2", TKDPO4K_ATTR_DATA_YOFF) );  
			checkErr( Ivi_InvalidateAttribute (vi, "REF3", TKDPO4K_ATTR_DATA_YOFF) );
