			checkErr( Ivi_InvalidateAttribute (vi, "REF2", TKDPO4K_ATTR_DATA_YMULT) );		
			checkErr( Ivi_InvalidateAttribute (vi, "REF3", TKDPO4K_ATTR_DATA_YMULT) );
