        checkErr( tkdpo4k_SetCachingOnProbeDependentAttributes (vi, VI_FALSE));
        checkErr( Ivi_SetAttributeViBoolean (vi, channelName, TKDPO4K_ATTR_PROBE_SENSE, 
                                             0, VI_TRUE));
