#define SET_BIT(bit_array, bit_position)	*((ViChar*) (bit_array) + ((bit_position) / 8))\
											|=\
											( 1 << ((bit_position) % 8))

#define RESET_BIT(bit_array, bit_position)	*((ViChar*) (bit_array) + ((bit_position) / 8))\
											&=\
											~( 1 << ((bit_position) % 8))

#define CHECK_BIT(bit_array, bit_position)	(*((ViChar*) (bit_array) + ((bit_position) / 8))\
											&\
											( 1 << ((bit_position) % 8)))

