	ViBoolean	CDMA_enabled = VI_FALSE;
	ViBoolean	TDMA_enabled = VI_FALSE;
	ViBoolean	arb_enabled = VI_FALSE;
	ViBoolean	external_enabled = VI_FALSE;

	checkErr( Ivi_GetAttributeViInt32( vi, VI_NULL, AGESG_ATTR_IQ_SOURCE, 0, &source));

