	viCheckErr( Ivi_CompareWithPrecision (14, value, 0.0, &result));
	if (result == 0)
		viCheckErr( viPrintf( io, ":RAD:ARB:RFIL THR"));
	else
		viCheckErr( viPrintf( io, ":RAD:ARB:RFIL %Lf", value));

Error:
	return error;
