/*****************************************************************************
 * Function: RemoveSurroundingWhiteSpace
 * Purpose:  This function removes surrounding white space around a string
 *****************************************************************************/
static void RemoveSurroundingWhiteSpace(ViChar string[])
{
    ViChar  *start = string,
            *end;
    ViInt32 lengthWithoutLeadingWS;

    if (start)
        {
        while (*start && isspace((unsigned char)*start))
            start++;

        lengthWithoutLeadingWS = strlen( start);
        memmove( string, start, lengthWithoutLeadingWS);
        end = string + lengthWithoutLeadingWS;
        while (end > string && isspace((unsigned char)*(end-1)))
            end--;

        *end = '\0';
        }
}

/*****************************************************************************
 * Function: AddAttributeFlags
 * Purpose:  This function add new flags for attribute
 *****************************************************************************/
static ViStatus AddAttributeFlags(
	ViSession	vi,
	ViAttr		attributeId,
	ViInt32		newFlags
)
{
	ViStatus	error = VI_SUCCESS;
	ViInt32		flags = 0;

	checkErr( Ivi_GetAttributeFlags (vi, attributeId, &flags));

	flags |= newFlags;

	checkErr( Ivi_SetAttributeFlags(vi, attributeId, flags));

Error:
	return error;
}

/*****************************************************************************
 *  Function: agesg_QueryID
 *  Purpose:  Query the instrument for its id string.
 *****************************************************************************/
static ViStatus agesg_QueryID(
	ViSession vi,		/* Driver Session */
	ViSession io,		/* IO Session */
	ViInt32 bufferSize,	/* Size of buffer */
	ViChar buffer[]		/* buffer for ID string */
)
{
    ViStatus  error = VI_SUCCESS;
    ViUInt32  read_count;

    viCheckErr( viWrite (io, "*IDN?", 5, VI_NULL));
    viCheckErr( viRead (io, buffer, bufferSize-1, &read_count));
    buffer[read_count] = '\0';

Error:
    return error;
}

/*****************************************************************************
 *  Function: agesg_GetDriverSetupOption
 *  Purpose:  Get the value of a DriverSetup option. The driver setup string
 *            is part of the option string the user passes to the
 *            agesg_InitWithOptions function. Specifically it is the part
 *            that follows "DriverSetup=". This function assumes the
 *            DriverSetup string has the following format:
 *              "Model : OptionValue"
 *****************************************************************************/
static ViStatus agesg_GetDriverSetupOption (
	ViSession vi,
	ViInt32* pModelType,
	ViAddr options
)
{
    ViStatus	error = VI_SUCCESS;
	ViChar*		ptag;
	ViChar		setup[BUFFER_SIZE],
                model_buffer[BUFFER_SIZE],
                model[BUFFER_SIZE];

	/* Initialize output variables */
	*pModelType = NOT_SUPPORTED;

    checkErr( Ivi_GetAttributeViString (vi,
										VI_NULL,
										IVI_ATTR_DRIVER_SETUP,
										0, BUFFER_SIZE,
										setup));

    if ((ptag = strstr (setup, SETUP_MODEL_TAG)) != VI_NULL)
	{
        sscanf(ptag, "%*[^:]:%256[^;]", model_buffer);
        RemoveSurroundingWhiteSpace (model_buffer);
		if (Ivi_GetValueFromTable (agesg_ModelTable, model_buffer, pModelType) < VI_SUCCESS)
		{
            ViChar message[BUFFER_SIZE];

            sprintf (message, "Bad value for %s option in DriverSetup: %s",
					 SETUP_MODEL_TAG,
					 model_buffer);

            viCheckErrElab (IVI_ERROR_BAD_OPTION_VALUE, message);
		}
	}

	if ((ptag = strstr (setup, SETUP_OPTION_TAG)) != VI_NULL)
    {
        ViInt32	bit;

        sscanf(ptag, "%*[^:]:%256[^;]", setup);
        ptag = setup;
        do
        {
        	if (*ptag == '&') ptag++;
        	sscanf(ptag, "%256[^&]", model);
			RemoveSurroundingWhiteSpace (model);
	        if (Ivi_GetValueFromTable (agesg_OptionTable, model, &bit) < VI_SUCCESS)
	        {
	            ViChar buffer[BUFFER_SIZE];
	            sprintf (buffer, "Bad value for Option in DriverSetup: %s", model);
	            viCheckErrElab (IVI_ERROR_BAD_OPTION_VALUE, buffer);
	        }
			SET_BIT(options, bit);
		} while (ptag = strchr (ptag, '&'));
	}

Error:
    return error;
}

/*****************************************************************************
 * Function: agesg_GetInstalledOptions
 * Purpose:  This function parses option string and sets option bit array
 *****************************************************************************/
static ViStatus agesg_GetInstalledOptions(
	ViSession vi,
	ViSession io,
	ViAddr options
)
{
	ViStatus	error = VI_SUCCESS;
	ViChar		buffer[512];
	ViChar*		poption;

	memset(buffer, 0, sizeof(buffer));

	viCheckErr( viQueryf(io, ":DIAG:INFO:OPT?", "%512[^\n]", buffer));

	poption = strtok(buffer, ",");
	while (poption != VI_NULL)
	{
		ViInt32		bit_position;

		if (Ivi_GetValueFromTable (agesg_OptionTable, poption, &bit_position) < VI_SUCCESS)
		{
			/* new not supported option */
			poption = strtok(VI_NULL, ",");
			continue;
		}

		SET_BIT(options, bit_position);

		poption = strtok(VI_NULL, ",");
	}

Error:
	return error;
}

/*****************************************************************************
 * Function: agesg_GetModelFromBuffer
 * Purpose:  This function returns model number from buffer. If table
 *           agesg_ModelTable does not contains this model, function
 *           returns VI_ERROR_FAIL_ID_QUERY;
 *****************************************************************************/
static ViStatus agesg_GetModelFromBuffer(
	ViSession vi,				/* Driver Session */
	ViConstString buffer,       /* ID Query Buffer */
	ViInt32 *pModelType			/* Model type see ESupportedInstruments */
)
{
    ViStatus    error = VI_SUCCESS;
    ViChar      instrument_buffer[BUFFER_SIZE];

    sscanf(buffer, "%*[^,],%[^,]", instrument_buffer);

	RemoveSurroundingWhiteSpace(instrument_buffer);

	if (Ivi_GetValueFromTable (agesg_ModelTable, instrument_buffer, pModelType) < VI_SUCCESS)
        viCheckErr( VI_ERROR_FAIL_ID_QUERY);

Error:
    return error;
}

/*****************************************************************************
 * Function: agesg_CheckFrequencyLimit
 * Purpose:  This function checks range of frequency list.
 *           Returns 0 or index of invalid element + 1
 *****************************************************************************/
static ViStatus agesg_CheckFrequencyLimit(ViSession vi, ViInt32 size, ViReal64 frequencyList[])
{
	ViInt32		i;
	ViStatus	error = VI_SUCCESS;
	ViReal64	min,
				max,
				offset = 0.0,
				reference = 0.0;
	ViReal64	multiplier = 1;
	ViBoolean	enabled;

	checkErr( Ivi_GetAttrMinMaxViReal64 (vi, VI_NULL,
										 AGESG_ATTR_FREQUENCY_STEP_START,
										 &min, &max, VI_NULL, VI_NULL));

	checkErr( Ivi_GetAttributeViReal64 (vi, VI_NULL,
										AGESG_ATTR_FREQUENCY_OFFSET, 0,
										&offset));
	checkErr( Ivi_GetAttributeViReal64 (vi, VI_NULL,
									   AGESG_ATTR_FREQUENCY_MULTIPLIER_CW, 0,
									   &multiplier));
	checkErr( Ivi_GetAttributeViBoolean (vi, VI_NULL,
										 AGESG_ATTR_FREQUENCY_REFERENCE_ENABLED,
										 0, &enabled));
	if (enabled)
	{
		checkErr( Ivi_GetAttributeViReal64 (vi, VI_NULL,
											AGESG_ATTR_FREQUENCY_REFERENCE, 0,
											&reference));
	}

	min = (min * multiplier) + offset - reference * multiplier;
	max = (max * multiplier) + offset - reference * multiplier;

	for (i = 0; i < size; i++)
	{
		if (frequencyList[i] < min || frequencyList[i] > max)
			return i + 1;
	}

Error:
	return error;
}

/*****************************************************************************
 * Function: agesg_CheckPowerLimit
 * Purpose:  This function checks range of power list.
 *           Returns 0 or index of invalid element + 1
 *****************************************************************************/
static ViStatus agesg_CheckPowerLimit(ViSession vi, ViInt32 size, ViReal64 powerList[])
{
	ViInt32		i;
	ViStatus	error = VI_SUCCESS;
	ViReal64	min,
				max,
				offset = 0.0,
				reference = 0.0;
	ViBoolean	enabled;

	checkErr( Ivi_GetAttrMinMaxViReal64 (vi, VI_NULL,
										 AGESG_ATTR_POWER_STEP_START,
										 &min, &max, VI_NULL, VI_NULL));

	checkErr( Ivi_GetAttributeViReal64 (vi, VI_NULL,
										AGESG_ATTR_POWER_OFFSET, 0,
										&offset));
	checkErr( Ivi_GetAttributeViBoolean (vi, VI_NULL,
										 AGESG_ATTR_POWER_REFERENCE_ENABLED,
										 0, &enabled));
	if (enabled)
	{
		checkErr( Ivi_GetAttributeViReal64 (vi, VI_NULL,
											AGESG_ATTR_POWER_REFERENCE, 0,
											&reference));
	}

	min = min + offset - reference;
	max = max + offset - reference;

	for (i = 0; i < size; i++)
	{
		if (powerList[i] < min || powerList[i] > max)
			return i + 1;
	}

Error:
	return error;
}

/*****************************************************************************
 * Function: agesg_CheckARBWaveform
 * Purpose:  This function checks range of ARBWaveform
 *           Returns 0 or index of invalid element + 1
 *****************************************************************************/
static ViStatus agesg_CheckARBWaveform(
	ViSession vi,
	ViInt32 size,
	ViReal64 Data[]
)
{
	ViInt32	i;

	for (i = 0; i < size; i++)
	{
		if (fabs(Data[i]) > 1.0)
		{
			return i + 1;
		}
	}

	return 0;
}

/*****************************************************************************
 * Function: agesg_ConvertARBWaveform
 * Purpose:  This function converts ARBWaveform to unsigned short array
 *****************************************************************************/
static ViStatus agesg_ConvertARBWaveform(
	ViSession vi,
	ViInt32 NumberOfSamples,
	ViReal64 Data[],
	ViUInt16 convert[]
)
{
	ViInt32	i;

	for (i = 0; i < NumberOfSamples; i++)
	{
		convert[i] = (ViUInt16) floor((Data[i] + 1.0) * 8191.5);
	}

	return VI_SUCCESS;
}

/*****************************************************************************
 * Function: agesg_LoadList
 * Purpose:  This function loads sweep list into instrument.
 *
 *    parameters:  listName - Name of list data which will be stored
 *                             into instrument
 *                 frequencyList - array of frequency data, you can pass VI_NULL
 *                                 if no frequency list
 *                 powerList     - array of power data, you can pass VI_NULL
 *                                 if no power list
 *                 dwellList     - array of DWell data, you can pass VI_NULL
 *                                 if no DWell list
 *
 *****************************************************************************/
static ViStatus agesg_LoadList(
	ViSession vi,
	ViConstString listName,
	ViInt32 arraySize,
	ViReal64 frequencyList[],
	ViReal64 powerList[],
	ViReal64 dwellList[]
)
{
	ViStatus	error = VI_SUCCESS;
	ViSession   io = Ivi_IOSession(vi); /* call only when locked */
	ViChar*		pbinary;
	ViChar*		ptmp_binary;
	ViChar*		pbuffer_data;
	ViReal64	power,
				frequency;
	ViChar		buffer_size[BUFFER_SIZE],
				tmp_buffer[BUFFER_SIZE];
	ViInt32		size_all,
				size_frequency,
				size_power,
				size_dwell,
				dwell,
				free_memory,
				occupied_memory,
				added_size;
	ViInt32*	pdwellarray = VI_NULL;

	if (frequencyList == VI_NULL && powerList == VI_NULL)
	{
		viCheckErr( IVI_ERROR_INVALID_CONFIGURATION);
	}

	/* binary buffer size:
	   2 Bytes size of frequency list + frequency list data (size * 8 Bytes) +
	   2 Bytes size of power list + power list data (size * 8 Bytes) +
	   2 Bytes size of DWell list + DWell list data (size * 4 Bytes) */
	size_frequency = ((frequencyList != VI_NULL) ? arraySize : 1) * FREQUENCY_ITEM_LENGTH;
	size_power = ((powerList != VI_NULL) ? arraySize : 1) * POWER_ITEM_LENGTH;
	size_dwell = ((dwellList != VI_NULL) ? arraySize : 1) * DWELL_ITEM_LENGTH;

	size_all = LIST_SIZE_BYTES + size_frequency + size_power + size_dwell;
	/* calculate occupied memory size (64 B blocks) */
	occupied_memory = 64 * ((ViInt32) ceil(size_all / 64.0));

	/* check instrumet free memory */
	viCheckErr( viQueryf(io, ":MEM:FREE?", "%ld", &free_memory));
	if (occupied_memory > free_memory)
		viCheckErr( IVI_ERROR_SYS_RSRC_ALLOC);

	/* allocate binary buffer */
	checkErr( Ivi_Alloc (vi, size_all, (ViAddr *) &pbinary));

	/* Allocate buffer for data */
	if (frequencyList != VI_NULL)
	{
		sprintf(tmp_buffer, "%d", size_frequency);
		added_size = 2 + strlen(tmp_buffer);
		checkErr( Ivi_Alloc (vi, size_frequency + added_size, (ViAddr*) &pbuffer_data));
	}
	else if (powerList != VI_NULL)
	{
		sprintf(tmp_buffer, "%d", size_power);
		added_size = 2 + strlen(tmp_buffer);
		checkErr( Ivi_Alloc (vi, size_power + added_size, (ViAddr*) &pbuffer_data));
	}

	/* create frequency list size */
	viCheckErr( viSPrintf(io, buffer_size, "%1hb", &size_frequency));
	memmove (pbinary, buffer_size + 3, 2);
	ptmp_binary = pbinary + 2;

	/* frequency list data */
	if (frequencyList != VI_NULL)
	{
		viCheckErr( viSPrintf(io, (ViChar*) pbuffer_data, "%*Zb", arraySize, frequencyList));
	}
	else
	{
		/* one element - no frequency list */
		frequency = 1.0e+9; /* 1 GHz */
		viCheckErr( viSPrintf(io, (ViChar*) pbuffer_data, "%1Zb", &frequency));
		added_size = 3;
	}

	/* move only data block to binary buffer */
	memmove(ptmp_binary, pbuffer_data + added_size, size_frequency);
	ptmp_binary += size_frequency;

	/* power list size */
	viCheckErr( viSPrintf(io, buffer_size, "%1hb", &size_power));
	memmove (ptmp_binary, buffer_size + 3, 2);
	ptmp_binary += 2;

	/* power list data */
	if (powerList != VI_NULL)
	{
		viCheckErr( viSPrintf(io, (ViChar*) pbuffer_data, "%*Zb", arraySize, powerList));
	}
	else
	{
		/* one element - no power list */
		power = -135.0;  /* -135.0 dBm */
		viCheckErr( viSPrintf(io, (ViChar*) pbuffer_data, "%1Zb", &power));
		added_size = 3;
	}

	/* move only data block to binary buffer */
	memmove(ptmp_binary, pbuffer_data + added_size, size_power);
	ptmp_binary += size_power;

	/* DWell list size */
	viCheckErr( viSPrintf(io, buffer_size, "%1hb", &size_dwell));
	memmove (ptmp_binary, buffer_size + 3, 2);
	ptmp_binary += 2;

	/* power list data */
	if (dwellList != VI_NULL)
	{
		ViInt32	i;

		checkErr( Ivi_Alloc(vi, arraySize * sizeof(ViInt32), (ViAddr*) *pdwellarray));

		for (i = 0; i < arraySize; i++)
		{
			*(pdwellarray + i) = (ViInt32) floor(dwellList[i] * 1000000);
		}

		viCheckErr( viSPrintf(io, (ViChar*) pbuffer_data, "%*lb", arraySize, pdwellarray));
	}
	else
	{
		/* one element - no DWell list */
		dwell = 2000;	/* 2 miliseconds */
		viCheckErr( viSPrintf(io, (ViChar*) pbuffer_data, "%1lb", &dwell));
		added_size = 3;
	}

	/* move only data block to binary buffer */
	memmove(ptmp_binary, pbuffer_data + added_size, size_dwell);
	ptmp_binary += size_dwell;

	/* load data into instrument */
	viCheckErr( viPrintf(io, ":MEM:DATA \"LIST:%s\",%#*b",
							listName, size_all, pbinary));

Error:
	if (pbinary != VI_NULL)
		Ivi_Free (vi, pbinary);

	if (pbuffer_data != VI_NULL)
		Ivi_Free (vi, pbuffer_data);

	if (pdwellarray != VI_NULL)
		Ivi_Free(vi, pdwellarray);

	return error;
}

/*****************************************************************************
 * Function: agesg_AddListName
 * Purpose:  This function adds list name into linear list.
 *
 *    parameters:  Name  - Name of list
 *                 type  - type of stored list
 *                         LIST_TYPE_FREQUENCY - frequency list
 * 						   LIST_TYPE_POWER     - power list
 *						   LIST_TYPE_FREQUENCY_POWER - frequency&power list
 *						   LIST_TYPE_FREQUENCY_DWELL - frequency&dwell list
 *						   LIST_TYPE_POWER_DWELL - power&dwell list
 *						   LIST_TYPE_FREQUENCY_POWER_DWELL - frequency&power&dwell list
 *
 *****************************************************************************/
static ViStatus agesg_AddListName(
	ViSession vi,
	ViConstString Name,
	ViInt32 type
)
{
	ViStatus	error = VI_SUCCESS;
	ListNames*	first_list;
	ListNames*	new_item;

	checkErr( Ivi_GetAttributeViAddr (vi, VI_NULL,
									  AGESG_ATTR_INSTR_LIST_NAMES_STRUCT, 0,
									  (ViAddr*) &first_list));

	checkErr( Ivi_Alloc (vi, sizeof(ListNames), (ViAddr *)&new_item));

	checkErr( Ivi_Alloc (vi, strlen(Name) + 1, (ViAddr *)&(new_item->Name)));

	strcpy(new_item->Name, Name);
	new_item->type = type;
	new_item->next = VI_NULL;

	if (first_list != VI_NULL)
	{
		while (first_list->next != VI_NULL)
		{
			first_list = (ListNames*) first_list->next;
		}
		first_list->next = (ViAddr) new_item;
	}
	else
	{
		checkErr( Ivi_SetAttributeViAddr (vi, VI_NULL,
										  AGESG_ATTR_INSTR_LIST_NAMES_STRUCT, 0,
										  (ViAddr*) new_item));
	}

Error:
	return error;
}

/*****************************************************************************
 * Function: agesg_CheckListName
 * Purpose:  This function returns structure with information about list,
 *		     which is spedified in Name parameter
 *
 *    parameters:  Name - Name of list which you want return
 *				   list_item - pointer to structure with information
 *
 *****************************************************************************/
static ViStatus agesg_CheckListName(
	ViSession vi,
	ViConstString Name,
	ListNames** list_item
)
{
	ViStatus	error = VI_SUCCESS;
	ListNames*	first_list;

	*list_item = VI_NULL;

	checkErr( Ivi_GetAttributeViAddr (vi, VI_NULL,
									  AGESG_ATTR_INSTR_LIST_NAMES_STRUCT, 0,
									  (ViAddr*) &first_list));

	while (first_list != VI_NULL)
	{
		if (!strcmp(first_list->Name, Name))
		{
			*list_item = first_list;
			break;
		}
		first_list = (ListNames*) first_list->next;
	}

Error:
	return error;
}

/*****************************************************************************
 * Function: agseg_DeleteAllList
 * Purpose:  This function deletes all items in linear list
 *
 *****************************************************************************/
static ViStatus agseg_DeleteAllList(ViSession vi)
{
	ViStatus	error = VI_SUCCESS;
	ListNames*	first_item;

	checkErr( Ivi_GetAttributeViAddr (vi, VI_NULL,
									  AGESG_ATTR_INSTR_LIST_NAMES_STRUCT, 0,
									  (ViAddr*) &first_item));

	/* remove all list structure */
	while (first_item != NULL)
	{
		ListNames*	next;

		next = (ListNames*) first_item->next;

		checkErr( Ivi_Free (vi, first_item->Name));
		checkErr( Ivi_Free (vi, first_item));
		first_item = next;
	}

Error:
	return error;
}

/*****************************************************************************
 * Function: agesg_LoadARBWaveform
 * Purpose:  This function loads ARB waveform into instrument
 *
 *    parameters: MoreDataPending - specify loading type
 *                                  VI_TRUE  - leave open to add next waveform
 *				 				    VI_FALSE - no more data will be added
 *				  Name - waveform name
 * 				  size - size of waveform for upload into instrument
 *				  curr_size - size of waveform from instrument
 *				  IData - array of I Data
 *				  QData - array of Q Data
 *
 *****************************************************************************/
static ViStatus agesg_LoadARBWaveform(
	ViSession vi,
	ViBoolean MoreDataPending,
	ViConstString Name,
	ViInt32 size,
	ViInt32	curr_size,
	ViUInt16 IData[],
	ViUInt16 QData[]
)
{
	ViStatus 	error = VI_SUCCESS;
	ViBoolean	enabled;
	ViUInt16*	I_buffer = VI_NULL;
	ViUInt16*	Q_buffer = VI_NULL;

	checkErr( Ivi_GetAttributeViBoolean (vi, VI_NULL,
										 AGESG_ATTR_INSTR_ARB_ENABLED, 0,
										 &enabled));

	if (enabled)
	{
		/* Driver can not load waveform with same name as active selected waveform */
		ViChar	selected[BUFFER_SIZE];

		checkErr( Ivi_GetAttributeViString (vi, VI_NULL,
											AGESG_ATTR_ARB_SELECTED_WAVEFORM, 0,
											BUFFER_SIZE, selected));

		if (!strcmp(Name, selected))
		{
			viCheckErr( IVI_ERROR_INVALID_CONFIGURATION);
		}
	}

	if (agesg_isOpenWaveform(vi, Name))
	{
		if (curr_size == 0)
			viCheckErr(IVI_ERROR_INVALID_PARAMETER);

		if (!Ivi_Simulating(vi))
		{
			ViSession   io = Ivi_IOSession(vi); /* call only when locked */
			ViUInt16*	pbuffer;
			ViInt32		loaded_size;
			ViInt32		i;

			checkErr( Ivi_Alloc (vi, (curr_size + size) * sizeof(ViUInt16),
								 (ViAddr*) &I_buffer));
			checkErr( Ivi_Alloc (vi, (curr_size + size) * sizeof(ViUInt16),
								 (ViAddr*) &Q_buffer));

			/* download old part of waveform from instrument */
			loaded_size = curr_size;
			viCheckErr( viQueryf (io, ":MMEM:DATA? \"ARBI:%s\"", "%#hb",
								  Name, &loaded_size, I_buffer));

			loaded_size = curr_size;
			viCheckErr( viQueryf (io, ":MMEM:DATA? \"ARBQ:%s\"", "%#hb",
								  Name, &loaded_size, Q_buffer));

			/* add new part of waveform */
			pbuffer = I_buffer + curr_size;

			for (i = 0; i < size; i++, pbuffer++)
				*pbuffer = IData[i];

			pbuffer = Q_buffer + curr_size;

			for (i = 0; i < size; i++, pbuffer++)
				*pbuffer = QData[i];

			/* upload new waveform into instrument */
			checkErr( Ivi_SetNeedToCheckStatus (vi, VI_TRUE));
			viCheckErr( viPrintf (io, ":MMEM:DATA \"ARBI:%s\", %#*hb",
								  Name, size + curr_size, I_buffer));
			viCheckErr( viPrintf (io, ":MMEM:DATA \"ARBQ:%s\", %#*hb",
								  Name, size + curr_size, Q_buffer));

		}

		if (!MoreDataPending)
		{
			/* no more data come - remove waveform name from open waveform list */
			/*                     and add into loaded waveform list */
			checkErr( agesg_RemoveWaveformName(vi,
											   AGESG_ATTR_INSTR_ARB_OPEN_WAVEFORMS,
											   Name));

			checkErr( agesg_AddWaveformName(vi,
							  			    AGESG_ATTR_INSTR_ARB_LOADED_WAVEFORMS,
											Name));
		}
	}
	else
	{
		if (!Ivi_Simulating(vi))
		{
			ViSession   io = Ivi_IOSession(vi); /* call only when locked */

			/* upload waveform into instrument */
			checkErr( Ivi_SetNeedToCheckStatus (vi, VI_TRUE));
			viCheckErr( viPrintf (io, ":MMEM:DATA \"ARBI:%s\", %#*hb",
								  Name, size, IData));
			viCheckErr( viPrintf (io, ":MMEM:DATA \"ARBQ:%s\", %#*hb",
								  Name, size, QData));
		}

		if (MoreDataPending)
		{
			/* mode data are comming - add waveform name into open waveform list */
			/*                         and remove from loaded waveform list */
			checkErr( agesg_AddWaveformName(vi,
							  			    AGESG_ATTR_INSTR_ARB_OPEN_WAVEFORMS,
											Name));
			checkErr( agesg_RemoveWaveformName(vi,
											   AGESG_ATTR_INSTR_ARB_LOADED_WAVEFORMS,
											   Name));
		}

		else if (!agesg_isLoadedWaveform(vi, Name))
			checkErr( agesg_AddWaveformName(vi,
							  			    AGESG_ATTR_INSTR_ARB_LOADED_WAVEFORMS,
											Name));
	}

Error:
	return error;
}


/*****************************************************************************
 * Function: agesg_GetFreeMemory
 * Purpose:  This function returns free memory size in instrument memory
 *
 *    parameters:  Name  - waveform name
 *				   free_memory - free memory in instrument
 *				   curr_size - size of waveform in instrument
 *
 *****************************************************************************/
static ViStatus agesg_GetFreeMemory(
	ViSession vi,
	ViConstString Name,
	ViInt32* free_memory,
	ViInt32* curr_size
)
{
	ViStatus	error = VI_SUCCESS;
	ViChar		buffer[BUFFER_SIZE];
	ViChar*		pfree_memory;
	ViChar*		pfile_list;
	ViInt32		size = BUFFER_SIZE;
	ViInt32		waveform_size = 0;

	if (!Ivi_Simulating(vi))
	{
		ViSession   io = Ivi_IOSession(vi); /* call only when locked */

		checkErr( Ivi_SetNeedToCheckStatus (vi, VI_TRUE));
		viCheckErr( viQueryf (io, ":MMEM:CAT? \"ARBI:\"", "%#s",
							  &size, buffer));

		/* parse free memory from catalog response */
		pfree_memory = strtok(buffer, ",");
		pfree_memory = strtok(VI_NULL, ",");
		*free_memory = atoi(pfree_memory);

		while (pfile_list = strtok(VI_NULL, "\""))
		{
			ViChar*	pname;
			ViChar*	ptype;
			ViChar*	data_size;

			if (*pfile_list == ',')
				continue;

			pname = pfile_list;

			ptype = strchr(pfile_list, ',');
			*ptype++ = '\0';

			data_size = strchr(ptype, ',');
			*data_size++ = '\0';

			/* find waveform in istrument and return size of waveform */
			if (!strncmp(ptype, "ARB", 3) && !strcmp(pname, Name))
			{
				waveform_size = atoi(data_size);
				break;
			}

		}

		if (waveform_size != 0)
		{
			if (agesg_isOpenWaveform(vi, Name))
			{
				/* I data occupies 4096 B and Q data occupies 4096 B too */
				/* add free space witch occupies loaded waveform */
				*free_memory += 8192 - waveform_size % 8192;
			}
			else
			{
				/* add space witch occupies loaded waveform */
				/* old waveform will be removed */
				*free_memory += 8192 * ((ViInt32) ceil(waveform_size / 8192.0));
			}
		}
	}
	else
	{
		checkErr( Ivi_GetAttributeViInt32 (vi, VI_NULL,
										   AGESG_ATTR_ARB_WAVEFORM_SIZE_MAX, 0,
										   free_memory));
		*free_memory *= 2;
	}

Error:
	/* waveform size responding number of bytes for both I and Q wave */
	/* return number of element for one of I or Q data waveform */
	*curr_size = (waveform_size / sizeof(ViUInt16)) / 2;

	return error;
}

/*****************************************************************************
 * Function: agesg_isOpenWaveform
 * Purpose:  This function checks if waveform name is in open waveforms
 *           and waiting for another data.
 *
 *****************************************************************************/
static ViBoolean agesg_isOpenWaveform(ViSession vi, ViConstString Name)
{
	ViStatus	error = VI_SUCCESS;
	ViInt32		size;
	ViChar*		popen_waveforms = VI_NULL;
	ViChar*		pname;

	size = Ivi_GetAttributeViString (vi, VI_NULL,
									 AGESG_ATTR_INSTR_ARB_OPEN_WAVEFORMS,
									 0, 0, VI_NULL);

	/* Warning: remove when fixed IVI bug */
	size = 1024;
	/* ---------------------------------- */

	if (size == 0)
		checkErr( -1);

	checkErr( Ivi_Alloc(vi, size, (ViAddr*) &popen_waveforms));

	checkErr( Ivi_GetAttributeViString (vi, VI_NULL,
										AGESG_ATTR_INSTR_ARB_OPEN_WAVEFORMS, 0,
										size, popen_waveforms));

	error = -1;

	pname = strtok(popen_waveforms, ",");
	while (pname != VI_NULL)
	{
		if (!strcmp(pname, Name))
		{
			error = VI_SUCCESS;
			break;
		}
		pname = strtok(VI_NULL, ",");
	}

Error:
	if (popen_waveforms != VI_NULL)
		Ivi_Free(vi, popen_waveforms);

	if (error != VI_SUCCESS)
		return VI_FALSE;
	else
		return VI_TRUE;
}

